// TAB INIT // ----------------------------------------------------------
function tabInit()
{
	console.log('tabInit');
	
	// IMAGES PRELOAD // -----------------------------------
 	preloadImages(['images/arrowUp-over.png','images/arrowDown-over.png','images/novedades/btnFb-over.png','images/casting/btnCasting-over.png','images/acceso/btnParticipar-over.png','images/entradas/btnCentros-over.png']);
	// -----------------------------------------------------
	
	// HIDE BTN ENTER // -----------------------------------
	$('#btnEnter').hide();
	// -----------------------------------------------------
	
	// SPINNER // ------------------------------------------
	var opts = {
	  lines: 13, // The number of lines to draw
	  length: 7, // The length of each line
	  width: 4, // The line thickness
	  radius: 10, // The radius of the inner circle
	  corners: 1, // Corner roundness (0..1)
	  rotate: 0, // The rotation offset
	  color: '#2B63A7', // #rgb or #rrggbb
	  speed: 2, // Rounds per second
	  trail: 60, // Afterglow percentage
	  shadow: false, // Whether to render a shadow
	  hwaccel: false, // Whether to use hardware acceleration
	  className: 'spinner', // The CSS class to assign to the spinner
	  zIndex: 2e9, // The z-index (defaults to 2000000000)
	  top: 'auto', // Top position relative to parent in px
	  left: 'auto' // Left position relative to parent in px
	};
	
	var spinner = new Spinner(opts);
	var spinTarget = document.getElementById('spinner');
	spinner.spin(spinTarget);
	// -----------------------------------------------------
}
