
function formDefaultValues()
{var defaultValues={};$("input[type=text].defaultValue, textarea.defaultValue").each(function(){if($(this).attr('id')&&$(this).val()!=""){defaultValues[$(this).attr('id')]=$(this).val();}});$("input[type=text].defaultValue, textarea.defaultValue").focus(function(){if(defaultValues[$(this).attr('id')]&&defaultValues[$(this).attr('id')]!=""&&$(this).val()==defaultValues[$(this).attr('id')]){$(this).val("");}});$("input[type=text].defaultValue, textarea.defaultValue").blur(function(){if(defaultValues[$(this).attr('id')]&&defaultValues[$(this).attr('id')]!=""&&$(this).val()==''){$(this).val(defaultValues[$(this).attr('id')]);}});}
function makeImagesRollovers()
{var imgSrc_orig="";var imgSrc_over="";var imagesArr=[];$('img.rollover, input[type=image].rollover').each(function(){imgSrc_orig=$(this).attr('src');var imgSrc_arr=imgSrc_orig.split(".");imgSrc_arr[imgSrc_arr.length-1]="_over."+imgSrc_arr[imgSrc_arr.length-1];imgSrc_over=imgSrc_arr.join("");imagesArr.push(imgSrc_over);});if(imagesArr.length>0){preloadImages(imagesArr);}
$('img.rollover, input[type=image].rollover').hover(function(){$(this).attr('src',imgSrc_over);},function(){$(this).attr('src',imgSrc_orig);});}
function preloadImages(images)
{console.log('preloadImages: '+images);if(Object.prototype.toString.call(images)==='[object Array]'){if(document.images)
{var imageObj;for(var i=0;i<images.length;i++)
{imageObj=new Image();imageObj.src=images[i];}}}else{console.log('preloadImages - ERROR: must pass an array with images!');}}
function ucfirst(string)
{return string.charAt(0).toUpperCase()+string.slice(1);}
function ucwords(str)
{return(str+'').replace(/^([a-z])|\s+([a-z])/g,function($1){return $1.toUpperCase();});}
function getFlashMovieObject(movieName)
{if(window.document[movieName]){return window.document[movieName];}
if(navigator.appName.indexOf("Microsoft Internet")==-1){if(document.embeds&&document.embeds[movieName])
return document.embeds[movieName];}
else{return document.getElementById(movieName);}}